import React from 'react';
import firebaseConfig from '../../firebase.js';
import * as firebase from 'firebase/app';

class Tier1 extends React.Component {
    constructor(props){
        super(props);
        this.state={
            sources: [],
            names: []
        };
        this.db = firebase.firestore();     
    }

    render() {
        return(
            <div className="TierItem">
                <h1>1</h1>
                <p onChange={this.handleChange}>{this.state.names}</p>
            </div>
        )
        }

    componentDidMount() { 
        var vm = this
        var names = []
        this.db.collection("NBASources").where("score", "==" , 5).get().then((data) => {
            var sources = []
            data.forEach((doc) => {
                sources.push(doc.data())
            })
            vm.setState({ sources: sources })
        }).then(vm.fetchNames(vm.state.sources, names))
        // for (let source of this.state.sources) {
        //     
        //     console.log(source.Name);
        // }
        // this.state.sources.map(function(data){


        // })
        // console.log(names);
    }

    fetchNames(sources, names){   
        sources.forEach(function(source){
        names.push(source.Name)
        })
        this.setState({
            names: names
        })
    }


}


export default Tier1;
