import React from 'react';
import firebaseConfig from '../../firebase.js';
import * as firebase from 'firebase/app';

class Tier1 extends React.Component {
    constructor(props){
        super(props);
        this.state={
            sources: []
        };
        this.db = firebase.firestore();
    }

    componentDidMount() {
        this.db.collection("NBASources").where("name", "==" , true).then((data) => {
            this.setState({ sources: data })
        })
        }


    render(){
    return(
        <div className="TierItem">
            <h1>1</h1>
            <p>{this.state.name}</p>
        </div>
    )
    }
}


export default Tier1;
