class Tier1 extends React.Component {
    constructor(props){
        super(props);
        this.state={sources: []};
        var db = firebase.firestore();
    }

    componentDidMount() {
        this.db.get().then((data) => {
            this.setState({ sources: data })
        })
        }


    render(){
    return(
        <div className="TierItem">
            <h1>1</h1>
            <p>{this.state.name}</p>
        </div>
    )
    }
}


